import React from 'react';
import { Map, GoogleApiWrapper, Marker } from 'google-maps-react';
import { Container, Jumbotron, Row, Col, ListGroup, ListGroupItem } from 'react-bootstrap';


const mapStyles = {
    width: '100vh',
    height: '100%'
}

let myLatLng = {
    lat: '22.5726',
    lng: '88.3639'
};
   
class Dashboard extends React.Component {

    constructor(props) {
        super(props);
        console.log(this.props.username);
    }
    
    buttonClick(val){
        console.log(val);
        
        this.myLatLng = {
            lat : val.lat,
            lng : val.lng
        }
        if(this.props.google){
            console.log(this.props.google);
        }  
    }
    render() {
        let cityList = [
            {
                id : 1, 
                name : 'Delhi',
                lat : '28.7041',
                lng : '77.1025'
            },
            {
                id : 2, 
                name : 'Mumbai',
                lat : '19.0760',
                lng : '72.8777'
            },
            {
                id : 3, 
                name : 'Kolkata',
                lat : '22.5726',
                lng : '88.3639'
            },
            {
                id : 4,
                name : 'Channai',
                lat : '13.0827',
                lng : '80.2707'
            },
            {
                id : 5,
                name : 'Pune',
                lat : '18.5204',
                lng : '73.8567'
            },
            {
                id : 6, 
                name : 'Bangalore',
                lat : '12.9716',
                lng : '77.5946'
            },
            {
                id : 7,
                name : 'Hyderabad',
                lat : '17.3850',
                lng : '78.4867'
            },
            {
                id : 8,
                name : 'Kochi',
                lat : '9.9312',
                lng : '76.2673'
            },       
    
        ];
        let cityNames = cityList.map(data => {
            return (
                <li key={data.id}><a href="#" style={{ textDecoration: 'none' } } onClick={() => this.buttonClick(data)}>{data.name}</a></li>
            );
        })
        let displayMarkers = cityList.map(data => {
            return (
                <Marker key={data.id} position={{ lat : data.lat, lng : data.lng}} />
            );
        })
        var points = [
            { lat: 22.02, lng: 77.01 },
            { lat: 22.03, lng: 77.02 },
            { lat: 22.03, lng: 77.04 },
            { lat: 22.05, lng: 77.02 }
        ];
        var bounds = new this.props.google.maps.LatLngBounds();
        for (var i = 0; i < points.length; i++) {
            bounds.extend(points[i]);
}

        return (
            <Container>
                <Jumbotron>
                    <h3>Hello User {this.props.username} </h3>
                </Jumbotron>
                <Row>
                    <Col>                        
                        <h3>Lists Of Cities</h3>
                        <ListGroup>
                            <ListGroupItem active tag="a" href="#" action></ListGroupItem>
                        </ListGroup>
                        { /*<ul>
                    {cityNames}
                </ul> */}
                    </Col>
                    <Col>
                        <Map id="mymap"
                            google={this.props.google}
                            zoom={5}
                            style={mapStyles}
                            initialCenter={{ lat: 22.5726, lng: 77.3639 }}
                            bounds={bounds}
                        >
                            {/* <Marker position={myLatLng} />
                    <Marker position={{ lat : 19.0760,lng : 72.8777  }} /> */}
                            {displayMarkers}
                        </Map>
                    </Col>
                </Row>               
            </Container>
        );
    }
}
export default GoogleApiWrapper({
    apiKey: 'AIzaSyAwu42ZjnzWV4-LQe5-t72Ujt3OykdqZ_w'
  })(Dashboard);
