import React from 'react';
import ReactDOM from 'react-dom';
import { withRouter, Redirect } from "react-router";
import { Dashboard } from "./dashboard.js";
export default class Login extends React.Component {

    constructor(props) {
        super(props);
        this.handleSignIn = this.handleSignIn.bind(this);
      }
    
    state = {
        username: '',
        password: '',
    }   
   
    handleSignIn(e){
        e.preventDefault();
        this.state.username = this.refs.username.value
        this.state.password = this.refs.password.value
        if(this.state.username === this.state.password){
            console.log(this.state.username);
            document.getElementById('message').textContent = '';
            return <Redirect to={{
                pathname : '/Dashboard',
                state: {username: this.username }
            }}/>;

            
        } else {
            document.getElementById('message').textContent = 'please enter valid credential';
        }
        
        //this.props.onSignIn(this.username,this.password)
    }

    render() {
       return (
           <form onSubmit={this.handleSignIn.bind(this)}>
               <p>Login Form</p>
               <input type="text" ref="username" placeholder="username" /><br />
               <input type="password" ref="password" placeholder="password"  /><br />
               <p id="message"></p>
               <input type="submit" value="login"/>
           </form>
       );
    }
}
ReactDOM.render(
    <Router>
      <Route path="/dashboard/:username" component={Dashboard} />
    </Router>,
    node
  );
