import React from 'react';
import { withRouter } from "react-router";
import { Dashboard } from "./dashboard.js";
class Login extends React.Component {

    
    
    state = {
        username: '',
        password: '',
    }   
   
    handleSignIn(e){
        e.preventDefault();
        this.state.username = this.refs.username.value
        this.state.password = this.refs.password.value
        if(this.username === this.password){
            console.log(this.username);
            document.getElementById('message').textContent = '';
            // const location = {
            //     pathname : '/dashboard',
            //     state : { from: {pathname: '/Dashboard'} }
            // }
            // var {from} = this.props.location.state || {from: {pathname: '/'}}
            // this.props.push()
            
        } else {
            document.getElementById('message').textContent = 'please enter valid credential';
        }
        
        //this.props.onSignIn(this.username,this.password)
    }

    render() {
       return (
           <form onSubmit={this.handleSignIn.bind(this)}>
               <p>Login Form</p>
               <input type="text" ref="username" placeholder="username" /><br />
               <input type="password" ref="password" placeholder="password"  /><br />
               <p id="message"></p>
               <input type="submit" value="login"/>
           </form>
       );
    }
}

export default withRouter({
})(Login);
